KubePodInventory 
| where Computer in ({Computer})
| distinct Computer, PodUid, Name, PodRestartCount
//| extend PodName=Name
| where PodRestartCount > 10
//| order by PodRestartCount desc
//| limit 10

let row = dynamic({Row});
let contlog=
ContainerLog
| where Computer in ({Computer})
| extend LogJson = iff(LogEntry startswith "{", "Y", "N")
| extend json=iff(LogJson == "Y", parse_json(todynamic(LogEntry)), "")
| extend LogLevel=iff(LogJson == "Y" and json contains "Loglevel", tostring(todynamic(json).LogLevel), "")
| extend Level=iff(LogJson == "Y" and json contains "level", tostring(todynamic(json).level), "")
| extend Level=iff(Level=="", LogLevel, Level);
KubePodInventory
| where Computer in ({Computer})
//| extend PodName=Name
| where '{Row}' == '{}' or (PodUid == row.PodUid and Computer == row.Computer and PodRestartCount == row.PodRestartCount and Name == row.Name)
| distinct Computer, ContainerID, PodUid, Name, PodRestartCount
//| extend PodName=Name
| join contlog on ContainerID
| project Computer, ContainerID, PodUid, Name, PodRestartCount, LogEntry, LogJson, Level
| extend Level = iff(Level=="", "Other", iff(Level=="warning", "Warning", iff(Level=="info","Information", iff(Level=="error","Error", Level))))
//| distinct Computer, ContainerID, PodUid, PodRestartCount, Level
| summarize count(Level) by Level

